let cart = {};


function saveCart(){
  localStorage.setItem(STORAGE_KEY, JSON.stringify(cart));
  renderCartCount();
}


function loadCart(){
  const stored = localStorage.getItem(STORAGE_KEY);
  cart = stored ? JSON.parse(stored) : {};
  renderCartCount();
}


function renderCartCount(){
  const countElList = document.querySelectorAll('#cart-count');
  const total = Object.values(cart).reduce((s,i)=>s+i.qty,0);
  countElList.forEach(e=>{
    e.textContent = total;
  });
}


function formatRupee(n){
  return '₹' + n.toLocaleString('en-IN');
}


function addToCart(productId){
  const prod = productList.find(p=>p.id===productId);
  if(!prod) return;
  if(cart[productId]){
    cart[productId].qty += 1;
  } else {
    cart[productId] = { id: productId, name: prod.name, price: prod.price, unit: prod.unit, qty: 1 };
  }
  saveCart();
  animateAdd();
}


function animateAdd(){
  const btn = document.querySelector('.cart-button');
  if(!btn) return;
  btn.animate([{transform:'scale(1)'},{transform:'scale(1.06)'},{transform:'scale(1)'}],{duration:240});
}


function createProductCard(prod){
  const card = document.createElement('div');
  card.className = 'product-card';
  const img = document.createElement('img');
  img.className = 'product-image';
  img.src = prod.image;
  img.alt = prod.name;
  img.loading = 'lazy';
  img.onerror = ()=>{ img.src = 'https://images.unsplash.com/photo-1547592166-1a1d3b4d2d9b?auto=format&fit=crop&w=900&q=60' };
  const tag = document.createElement('div');
  tag.className = 'tag';
  tag.textContent = prod.tag;
  const name = document.createElement('h4');
  name.className = 'product-name';
  name.textContent = prod.name;
  const desc = document.createElement('p');
  desc.className = 'product-desc';
  desc.textContent = `${prod.unit} • ${prod.category}`;
  const bottom = document.createElement('div');
  bottom.className = 'card-row';
  const price = document.createElement('div');
  price.className = 'price';
  price.textContent = formatRupee(prod.price);
  const btn = document.createElement('button');
  btn.className = 'add-btn';
  btn.textContent = 'Add';
  btn.onclick = ()=> addToCart(prod.id);
  bottom.appendChild(price);
  bottom.appendChild(btn);
  card.appendChild(img);
  card.appendChild(tag);
  card.appendChild(name);
  card.appendChild(desc);
  card.appendChild(bottom);
  return card;
}


function renderProductsGrid(container, products){
  container.innerHTML = '';
  if(products.length === 0){
    const empt = document.createElement('p');
    empt.style.color = 'var(--muted)';
    empt.textContent = 'No products match your search.';
    container.appendChild(empt);
    return;
  }
  const fragment = document.createDocumentFragment();
  products.forEach(p=>{
    fragment.appendChild(createProductCard(p));
  });
  container.appendChild(fragment);
}


function setupFeatured(){
  const featured = document.getElementById('featured-grid');
  if(!featured) return;
  const picks = productList.slice(0,8);
  renderProductsGrid(featured,picks);
}


function setupProductsPage(){
  const grid = document.getElementById('products-grid');
  if(!grid) return;
  renderProductsGrid(grid, productList);
  const search = document.getElementById('search-input');
  const chips = document.getElementById('category-chips');
  if(search){
    search.addEventListener('input', ()=>{
      applyFilters();
    });
  }
  if(chips){
    chips.addEventListener('click', (e)=>{
      const btn = e.target.closest('button[data-cat]');
      if(!btn) return;
      document.querySelectorAll('.chip').forEach(c=>c.classList.remove('active'));
      btn.classList.add('active');
      applyFilters();
    });
  }
}


function applyFilters(){
  const search = document.getElementById('search-input');
  const q = search ? search.value.trim().toLowerCase() : '';
  const activeChip = document.querySelector('.chip.active');
  const cat = activeChip ? activeChip.getAttribute('data-cat') : 'All';
  let results = productList.filter(p=>{
    const matchesQ = !q || p.name.toLowerCase().includes(q) || p.category.toLowerCase().includes(q);
    const matchesCat = cat === 'All' || p.category === cat;
    return matchesQ && matchesCat;
  });
  const grid = document.getElementById('products-grid');
  if(grid) renderProductsGrid(grid, results);
}


function openCartPanel(){
  const panel = document.getElementById('cart-panel');
  panel.classList.add('open');
  renderCartPanel();
}


function closeCartPanel(){
  const panel = document.getElementById('cart-panel');
  panel.classList.remove('open');
}


function renderCartPanel(){
  const itemsEl = document.getElementById('cart-items');
  itemsEl.innerHTML = '';
  const keys = Object.keys(cart);
  if(keys.length === 0){
    const empty = document.createElement('div');
    empty.style.padding = '24px';
    empty.style.color = 'var(--muted)';
    empty.textContent = 'Your cart is empty. Add fresh groceries to get started.';
    itemsEl.appendChild(empty);
    updateCartTotal();
    return;
  }
  keys.forEach(key=>{
    const c = cart[key];
    const row = document.createElement('div');
    row.className = 'cart-item';
    const info = document.createElement('div');
    info.className = 'ci-info';
    const title = document.createElement('div');
    title.style.fontWeight = 700;
    title.textContent = c.name;
    const meta = document.createElement('div');
    meta.style.color = 'var(--muted)';
    meta.style.fontSize = '13px';
    meta.textContent = `${formatRupee(c.price)} • ${c.qty} x`;
    info.appendChild(title);
    info.appendChild(meta);
    const controls = document.createElement('div');
    controls.className = 'qty-controls';
    const minus = document.createElement('button');
    minus.textContent = '-';
    minus.onclick = ()=>{
      changeQuantity(c.id, -1);
    };
    const qty = document.createElement('div');
    qty.textContent = c.qty;
    qty.style.minWidth = '26px';
    qty.style.textAlign = 'center';
    const plus = document.createElement('button');
    plus.textContent = '+';
    plus.onclick = ()=>{
      changeQuantity(c.id, 1);
    };
    controls.appendChild(minus);
    controls.appendChild(qty);
    controls.appendChild(plus);
    const price = document.createElement('div');
    price.style.fontWeight = 800;
    price.textContent = formatRupee(c.price * c.qty);
    row.appendChild(info);
    row.appendChild(controls);
    row.appendChild(price);
    itemsEl.appendChild(row);
  });
  updateCartTotal();
}


function changeQuantity(productId, delta){
  if(!cart[productId]) return;
  cart[productId].qty += delta;
  if(cart[productId].qty <= 0){
    delete cart[productId];
  }
  saveCart();
  renderCartPanel();
}


function updateCartTotal(){
  const totalEl = document.getElementById('cart-total-amt');
  const total = Object.values(cart).reduce((s,i)=>s + (i.price * i.qty), 0);
  if(totalEl) totalEl.textContent = formatRupee(total);
}


function attachHeaderCartButtons(){
  document.querySelectorAll('#cart-button').forEach(btn=>{
    btn.addEventListener('click', openCartPanel);
  });
  document.querySelectorAll('#close-cart').forEach(b=>{
    b.addEventListener('click', closeCartPanel);
  });
  document.querySelectorAll('#checkout-btn').forEach(b=>{
    b.addEventListener('click', ()=>{
      alert('Proceeding to checkout is not implemented. This is a demo.');
    });
  });
}


function setupContactForm(){
  const formBtn = document.getElementById('contact-submit');
  if(!formBtn) return;
  formBtn.addEventListener('click', ()=>{
    formBtn.disabled = true;
    setTimeout(()=>{
      alert('Thank you for your message. Our support will reach out to you shortly.');
      formBtn.disabled = false;
      const name = document.getElementById('contact-name');
      const email = document.getElementById('contact-email');
      const msg = document.getElementById('contact-message');
      if(name) name.value = '';
      if(email) email.value = '';
      if(msg) msg.value = '';
    },650);
  });
}


function init(){
  loadCart();
  setupFeatured();
  setupProductsPage();
  attachHeaderCartButtons();
  setupContactForm();
}


document.addEventListener('DOMContentLoaded', init);
